import 'package:flutter/material.dart';import 'main_page.dart';// также, как и MainPage наша страница// не будет иметь состоянияclass CatsDetailPage extends StatelessWidget {  // в качестве параметра мы будет получать id категории  final int catId;  // конструктор CatsDetailPage принимает catId,  // который будет присвоен нашему ранее  // объявленному полю  CatsDetailPage(this.catId);  @override  Widget build(BuildContext context) {    // получаем категорию по его id    // обратите внимание: мы импортируем cats    // из файла main_page.dart    final cat = cats[catId];    return Scaffold(        appBar: AppBar(            title: Text(cat.name, style: TextStyle(                fontSize: 20, fontWeight: FontWeight.w500, color: Colors.black),),),        body: Padding(          // указываем отступ для контента          padding: EdgeInsets.all(15),          // Column размещает дочерние виджеты в виде колонки          // crossAxisAlignment - выравнивание по ширине (колонка) или          // по высоте (строка)          // mainAxisAlignment работает наоборот          // в данном случае мы растягиваем дочерние элементы          // на всю ширину колонки          child: Column(            crossAxisAlignment: CrossAxisAlignment.stretch,            children: [              Container(                  padding: EdgeInsets.all(10),                  child: Text(                    // указываем описание cat                      cat.desc,                      style: Theme.of(context).textTheme.bodyText1                  )              )            ],          ),        )    );  }}